import React from 'react';
import './App.css';

class App extends React.Component{
  constructor(props){
    super(props)
    this.state = {
      value: "",
      test: ""
    }
    this.handleSubmit = this.handleSubmit.bind(this)
    this.handleChange = this.handleChange.bind(this)
  }

  handleChange(event){
    this.setState({
      value: event.target.value
    })
  }

  handleSubmit(event){
    fetch('localhost:3000/') //new/' + this.state.value)
    .then(res=>{return res}).then(data=>{
      this.setState({
        test: data 
      })
    })
  }

  render(){
    return(
      <div>
        <h1>URL Shortening microservice</h1>
        <p>On this site you can add in a URL and our server will shortern it for you.</p>
        <form onSubmit={this.handleSubmit}>
          <input placeholder = 'Enter URL here...' value = {this.state.value} onChange={this.handleChange} />
          <button type='submit' value='submit' />
          <p>{this.state.test}</p>
        </form>
      </div>
    )
  }

}

export default App;
