import React from 'react';
import './App.css';

class App extends React.Component{
  constructor(props){
    super(props)
    this.state = {
      url: "",
      urlValue: ''
    }
    this.handleClick = this.handleClick.bind(this)
    this.onChange = this.onChange.bind(this)
  }

  onChange(event){
    this.setState({
      urlValue: event.target.value
    })
  }

  handleClick(event){
    this.setState({
      url: this.state.urlValue
    })
  }

  render(){
    return(
      <div>
        <h1>URL Shortening microservice</h1>
        <p>On this site you can add in a URL and our server will shortern it for you.</p>
        <form>
          <input placeholder = 'Enter URL here...' onChange={this.state.onChange} />
          <button type='submit' value='submit' onClick={this.handleClick} />
          <p>{this.state.url}</p>
        </form>
      </div>
    )
  }

}

export default App;
