import React from 'react';
import './App.css';

class App extends React.Component{
  constructor(props){
    super(props)
    this.state = {
      value: "",
    }
    this.handleClick = this.handleClick.bind(this)
    this.handleChange = this.handleChange.bind(this)
  }

  handleChange(event){
    this.setState({
      urlValue: event.target.value
    })
  }

  handleSubmit(event){
    // this.setState({
    //   url: this.state.urlValue
    // })
    console.log('hello')
  }

  render(){
    return(
      <div>
        <h1>URL Shortening microservice</h1>
        <p>On this site you can add in a URL and our server will shortern it for you.</p>
        <form onSubmit={this.handleSubmit}>
          <input placeholder = 'Enter URL here...' value = {this.state.value} onChange={this.handleChange} />
          <button type='submit' value='submit' />
          <p>{this.state.urlValue}</p>
        </form>
      </div>
    )
  }

}

export default App;
