import React from 'react';
import './App.css';

class App extends React.Component{
  constructor(props){
    super(props)
    this.state = {
      url: ""
    }
    this.handleClick = this.handleClick.bind(this)
  }

  handleClick(event){
    this.setState({
      url: event.target.submit()
    })
  }

  render(){
    return(
      <div>
        <h1>URL Shortening microservice</h1>
        <p>On this site you can add in a URL and our server will shortern it for you.</p>
        <form>
          <input placeholder = 'Enter URL here...'></input>
          <button type='submit' onClick={this.handleClick}>Shortern URL</button>
          <p>{this.state.url}</p>
        </form>
      </div>
    )
  }

}

export default App;
